public class DistinctSubsequence {

    public int numDistinct(String s, String t) {
        char[] tc = t.toCharArray();
        int tl = tc.length;
        char[] sc = s.toCharArray();
        int sl = sc.length;
        if (sl == 0) {
            return 0;
        }
        if (tl == 0) {
            return 1;
        }

        int[][] dp = new int[tl][sl];
        dp[0][0] = tc[0] == sc[0] ? 1 : 0;
        for (int i = 1; i < sl; i++) {
            dp[0][i] = dp[0][i - 1] + (tc[0] == sc[i] ? 1 : 0);
        }
        for (int i = 1; i < tl; i++) {
            dp[i][0] = 0;
        }
        for (int r = 1; r < tl; r++) {
            for (int c = 1; c < sl; c++) {
                if (tc[r] == sc[c]) {
                    dp[r][c] = dp[r - 1][c - 1] + dp[r][c - 1];
                } else {
                    dp[r][c] = dp[r][c - 1];
                }
            }
        }
        return dp[tl - 1][sl - 1];
    }

    @Deprecated // StackOverflow
    private int countPath(int[][] dp, char[] tc, char[] sc, int r, int c, int target, int[][]
            path) {
        if (path[r][c] > 0) {
            return path[r][c];
        }
        if (r == 0) {
            int res = 0;
            for (int i = 0; i <= c; i++) {
                if (tc[0] == sc[i]) res++;
            }
            path[r][c] = res;
            return res;
        }
        if (c == 0) {
            return 0;
        }
        int res = 0;
        if (tc[r] == sc[c]) {
            res += countPath(dp, tc, sc, r - 1, c - 1, target - 1, path);
            if (dp[r - 1][c] == target) {
                res += countPath(dp, tc, sc, r - 1, c, target, path);
            }
            if (dp[r][c - 1] == target) {
                res += countPath(dp, tc, sc, r, c - 1, target, path);
            }
        } else {
            if (dp[r][c - 1] == target) {
                res = countPath(dp, tc, sc, r, c - 1, target, path);
            } else {
                res = countPath(dp, tc, sc, r - 1, c, target, path);
            }
        }
        path[r][c] = res;
        return res;
    }

    public static void main(String[] args) {
        DistinctSubsequence sub = new DistinctSubsequence();
        System.out.println(sub.numDistinct("aabb", "ab"));
        System.out.println(sub.numDistinct("aacaacca", "ca"));
        System.out.println(sub.numDistinct
                (""
                        , "rwmimatmhydhbujebqehjprarwfkoebcxxqfktayaaeheys"));
        System.out.println(sub.numDistinct("rabbbbit", "rabbit"));
        System.out.println(sub.numDistinct("rabbbit", "rabbit"));
        System.out.println(sub.numDistinct("ab", "ca"));
        System.out.println(sub.numDistinct("ccc", "c"));
        System.out.println(sub.numDistinct("ccc", "cc"));
        System.out.println(sub.numDistinct("", "rabbit"));
        System.out.println(sub.numDistinct("aaa", ""));
    }
}
